import './styles/controller.css';

import Button from '@material-ui/core/Button';
import React from 'react';
import { get } from 'lodash';

class GameController extends React.Component {

  constructor(props) {
    super(props);
    this.onPlayClick = this.onPlayClick.bind(this);
  }

  onPlayClick() {
    console.log(this.props);
    this.props.onPlayBtnClick();
  }

  render() {
    console.log(this.props);
    let isGameOver = get(this.state,'gameOver',false);
    let isGameStarted = get(this.state,'gameStarted',false);
    return (
      <div className='controllerBtnContainer'>
        <Button variant='contained'
                color='secondary'
                className='controllerBtn'
                onClick={this.onPlayClick}>
          {(isGameStarted && !isGameOver) ? 'Reset' : 'Play'}
        </Button>
      </div>
    )
  }
}

export default GameController;
