import './styles/modal.css';
import React from 'react';
import {get} from 'lodash';

class ModalForm extends React.Component {

  constructMsg1() {
    let winnerFound = get(this.props,'winnerFound',false);
    let isGameOver = get(this.props,'isGameOver',false);
    let message = '';
    if(winnerFound) {
      message = 'Congratulation! ' + <br/> + 'We have a winner!';
    } else if(isGameOver) {
      message = 'Oops! ' + <br /> + 'Time up, try again.'
    } else {
      message = 'Wow, it was a tie! Try again'
    }
    return message;
  }

  constructMsg2() {

  }

  render() {
    let showModal = true;//get(this.props,'showModal',false);
    console.log('in modal form: ', this.props);
    return(
      <div className={showModal ? 'showModalContainer' : 'hideModalContainer'}>
        <div className='transparent'></div>
        <section className='modalBody'>
          <div>{this.constructMsg1}</div>
          <div></div>
        </section>
      </div>
    )
  }
}

export default ModalForm;
