import Header from './header';
import Content from './content';
import Footer from './footer';

import { get } from 'lodash';

var React = require('react');

const sybmol = {
  'player_1' : 'X',
  'player_2' : 'O'
};

class Page extends React.Component {

  constructor(props) {
    super(props);
    this.state = {
      matrix : [[],[],[]],
      player : '',
    };
    this.handleTileClick = this.handleTileClick.bind(this);
  }

  componentDidMount() {
    this.setState(() => {
      return {
        player : 'player_1',
        start: false,
        reset: false,
      }
    });
  }

  handleTileClick(row, col, player) {
    //console.log('clicked:',row, col, player);
    this.setMatrix(row, col, player);
    this.setState(() => {
      return {
        player: player === 'player_1' ? 'player_2' : 'player_1',
      }
    })
  }

  setMatrix(row, col, player) {
    let matrix = get(this.state,'matrix', [[],[],[]]);
    if(!matrix[row][col]) {
      matrix[row][col] = sybmol[player];
    } else {
      console.log('Click an empty box!');
    }
    this.setState(() => {
      return {
        matrix,
      }
    }, this.checkForWinner(matrix, row, col, player));
    console.log(matrix);
  }

  checkForWinner(matrix, row, col, player) {
    console.log('checkForWinner --- ', matrix);
    const symbol = sybmol[player];
    let winnerFound = get(this.state, 'winnerFound', false);
    if(this.checkHorizontal(matrix, row, col)) {
      console.log('checkHorizontal');
      winnerFound = !winnerFound;
    } else if(this.checkVertical(matrix, row, col)) {
      console.log('checkVertical');
      winnerFound = !winnerFound;
    } else if(this.checkDiagonal(matrix, row, col)) {
      console.log('checkDiagonal');
      winnerFound = !winnerFound;
    } else {
      console.log('continue playing!');
    }
    console.log('winnerFound ----- ', winnerFound);
    this.setState(() => {
      return {
        winnerFound : winnerFound
      }
    })
  }

  checkHorizontal(matrix, row, col) {
    let matched = false;
    const currentVal = matrix[row][col];
    if(col === 0) {
      matched = (currentVal === matrix[row][col+1]) && (currentVal === matrix[row][col+2]);
    } else if(col === 1) {
      matched = (currentVal === matrix[row][col-1]) && (currentVal === matrix[row][col+1]);
    } else {
      matched = (currentVal === matrix[row][col-1]) && (currentVal === matrix[row][col-2]);
    }
    return matched;
  }

  checkVertical(matrix, row, col) {
    let matched = false;
    const currentVal = matrix[row][col];
    if(row === 0) {
      matched = (currentVal === matrix[row+1][col]) && (currentVal === matrix[row+2][col]);
    } else if(row === 1) {
      matched = (currentVal === matrix[row-1][col]) && (currentVal === matrix[row+1][col]);
    } else {
      matched = (currentVal === matrix[row-1][col]) && (currentVal === matrix[row-2][col]);
    }
    return matched;
  }

  checkDiagonal(matrix, row, col) {
    let matched = false;
    const currentVal = matrix[row][col];
    if(row === 1 && col === 1) {
      matched = ((currentVal === matrix[row-1][col-1]) &&
                  (currentVal === matrix[row+1][col+1])) ||
                ((currentVal === matrix[row-1][col+1]) &&
                  (currentVal === matrix[row+1][col-1]));
    }
    return matched;
  }

  render() {
    console.log('RENDER: ', this.state);
    return(
      <div style={{'background':'black', 'opacity':0.8}}>
        <Header />
        <Content
           handleTileClick={this.handleTileClick}
           matrixContent={this.state.matrix}
           player={this.state.player}
         />
        <Footer />
      </div>
    );
  }
}

export default Page;
