import Header from './header';
import Content from './content';
import Footer from './footer';
var React = require('react');

const player1_symbol = 'X';
const player2_symbol = 'O';

class Page extends React.Component {

  constructor(props) {
    super(props);
    this.state = {
      matrix : [[]],
      player : '',
    };
    this.handleTileClick = this.handleTileClick.bind(this);
  }

  componentDidMount() {
    this.setState(() => {
      return {
        player : 'player_1',
        start: false,
        reset: false,
      }
    });
  }

  handleTileClick(row, col, player) {
    //console.log('clicked:',row, col, player);
    this.setMatrix(row, col, player);
    this.setState(() => {
      return {
        player: player === 'player_1' ? 'player_2' : 'player_1',
      }
    })
  }

  setMatrix(row, col, player) {
    console.log(row, col, player);
    let matrix = this.state.matrix;
    console.log('matrix: ', matrix);
    if(matrix[row][col]) {
      matrix[row][col] = (player === 'player_1' ? player1_symbol : player2_symbol);
      console.log('New matrix: ', matrix);
    } else {
      console.log('Click an empty box!');
    }
    this.setState(() => {
      return {
        matrix,
      }
    });
    console.log(matrix);
  }

  render() {
    return(
      <div style={{'background':'black', 'opacity':0.8}}>
        <Header />
        <Content
           handleTileClick={this.handleTileClick}
           matrixContent={this.state.matrix}
           player={this.state.player}
         />
        <Footer />
      </div>
    );
  }
}

export default Page;
